#include "Application.h"
#include <iostream>
#include <QTimer>
#include <QtConcurrentRun>

namespace
{
  const unsigned int NUM_SPAM_THREADS = 2;

  class Sleeper : public QThread
  {
    public:
      void msleep(unsigned int t) { QThread::msleep(t); }
  };

  void printA()
  {
    std::cout << "start A" << std::endl;
    Sleeper sleep;
    while (true)
    {
      std::cout <<
      "" << std::endl;
      sleep.msleep(0);
    }
  }

  void printB()
  {
    std::cout << "start B" << std::endl;
    Sleeper sleep;
    while (true)
    {
      std::cout <<
      "" << std::endl;
      sleep.msleep(0);
    }
  }
}

Application::Application(int argc, char** argv)
: QApplication(argc, argv)
{
  QtConcurrent::run(&printA);
  QtConcurrent::run(&printB);
  QtConcurrent::run(&printA);
  QtConcurrent::run(&printB);
}
